import React, { Component } from 'react';
import RoomList from './RoomList/RoomList';
import AddRoom from './RoomForms/AddRoom';
import { createChat, deleteChat, getChatPage, openUpdateRoom, closeUpdateRoom, updateRoom, fetchMoreRooms } from "./redux/roomActions";
import { connect } from "react-redux";
import { isLogged } from '../Auth/authService';
import UpdateRoom from './RoomForms/UpdateRoom';
import Container from '@material-ui/core/Container';

class RoomsContainer extends Component {

    componentDidMount() {
        this.props.getChatPage(0);
    }

    render() {
        const { chatState, createChat, deleteChat, getChatPage, openUpdateRoom, closeUpdateRoom, updateRoom, fetchMoreRooms, history } = this.props;
        const { roomListPage, pageNumber, totalPages, updatechatState, last } = chatState;

        const logged = isLogged();

        if (!logged) {
            history.push("/connexion")
        }

        return (
            <Container component="main" maxWidth="md" style={{ minHeight: `calc(100vh - 150px)`, marginTop: 150}}>
                <RoomList totalPages={totalPages} roomListPage={roomListPage} getChatPage={getChatPage} pageNumber={pageNumber} deleteChat={deleteChat} openUpdateRoom={openUpdateRoom} fetchMoreRooms={fetchMoreRooms} last={last} />
                <AddRoom createChat={createChat} getChatPage={getChatPage} pageNumber={pageNumber} />
                <UpdateRoom state={updatechatState} handleClose={closeUpdateRoom} updateRoom={updateRoom} />
            </Container>
        )
    }
}

const mapStateToProps = (state) => ({ chatState: state.rooms, navbarState: state.navbar });

const mapDispatchToProps = (dispatch) => ({
    createChat: (room) => dispatch(createChat(room)),
    updateRoom: (room) => dispatch(updateRoom(room)),
    deleteChat: (roomId) => dispatch(deleteChat(roomId)),
    getChatPage: (pageNumber) => dispatch(getChatPage(pageNumber)),
    fetchMoreRooms: (pageNumber) => dispatch(fetchMoreRooms(pageNumber)),
    openUpdateRoom: (room) => dispatch(openUpdateRoom(room)),
    closeUpdateRoom: () => dispatch(closeUpdateRoom()),
})

export default connect(mapStateToProps, mapDispatchToProps)(RoomsContainer);
