import React, { Component } from 'react';
import RoomList from './RoomList/RoomList';
import AddRoom from './RoomForms/AddRoom';
import { createChat, deleteChat, getChatPage, openUpdateChat, closeUpdateChat, updateChat, fetchMoreChats } from "./redux/roomActions";
import { connect } from "react-redux";
import { isLogged } from '../Auth/authService';
import UpdateChat from './RoomForms/UpdateChat';
import Container from '@material-ui/core/Container';

class RoomsContainer extends Component {

    componentDidMount() {
        this.props.getChatPage(0);
    }

    render() {
        const { chatState, createChat, deleteChat, getChatPage, openUpdateChat, closeUpdateChat, updateChat, fetchMoreChats, history } = this.props;
        const { chatPage, pageNumber, totalPages, updateChatState, last } = chatState;

        const logged = isLogged();

        if (!logged) {
            history.push("/connexion")
        }

        return (
            <Container component="main" maxWidth="md" style={{ minHeight: `calc(100vh - 150px)`, marginTop: 150}}>
                <RoomList totalPages={totalPages} chatPage={chatPage} getChatPage={getChatPage} pageNumber={pageNumber} deleteChat={deleteChat} openUpdateChat={openUpdateChat} fetchMoreChats={fetchMoreChats} last={last} />
                <AddRoom createChat={createChat} getChatPage={getChatPage} pageNumber={pageNumber} />
                <UpdateChat state={updateChatState} handleClose={closeUpdateChat} updateChat={updateChat} />
            </Container>
        )
    }
}

const mapStateToProps = (state) => ({ chatState: state.rooms, navbarState: state.navbar });

const mapDispatchToProps = (dispatch) => ({
    createChat: (room) => dispatch(createChat(room)),
    updateChat: (room) => dispatch(updateChat(room)),
    deleteChat: (roomId) => dispatch(deleteChat(roomId)),
    getChatPage: (pageNumber) => dispatch(getChatPage(pageNumber)),
    fetchMoreChats: (pageNumber) => dispatch(fetchMoreChats(pageNumber)),
    openUpdateChat: (room) => dispatch(openUpdateChat(room)),
    closeUpdateChat: () => dispatch(closeUpdateChat()),
})

export default connect(mapStateToProps, mapDispatchToProps)(RoomsContainer);
